package com.hackslash.collosaltitan;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;

import com.jackandphantom.carouselrecyclerview.CarouselLayoutManager;
import com.jackandphantom.carouselrecyclerview.CarouselRecyclerview;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        CarouselRecyclerview recyclerview=(CarouselRecyclerview) findViewById(R.id.recycler);
        recyclerview.set3DItem(true);
        recyclerview.setAlpha(true);
        recyclerview.setFlat(true);
        CarouselLayoutManager carouselLayoutManager=recyclerview.getCarouselLayoutManager();
        recyclerview.setLayoutManager(carouselLayoutManager);
        ArrayList<String> list=new ArrayList<>();
        list.add("https://m.media-amazon.com/images/M/MV5BYThjM2NlOTItYTUzMC00ODE3LTk1MTItM2I3MDViY2U3MThlXkEyXkFqcGdeQXVyMTg4NDI0NDM@._V1_UY1200_CR165,0,630,1200_AL_.jpg");
        list.add("data:image/jpeg;base64,");
        list.add("data:image/jpeg;base64,");
        list.add("data:image/jpeg;base64,");
        list.add("https://upload.wikimedia.org/wikipedia/commons/2/28/Zendaya_-_2019_by_Glenn_Francis.jpg");
        RecyclerAdapter adapter=new RecyclerAdapter(list,getApplicationContext());
        recyclerview.setAdapter(adapter);
        recyclerview.scrollToPosition(2);
    }
}